import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import TextField from "@material-ui/core/TextField";
import IconButton from "@material-ui/core/IconButton";
import Button from "@material-ui/core/Button";
import {
  NotificationContainer,
  NotificationManager,
} from "react-notifications";
import IntlMessages from "util/IntlMessages";
import CircularProgress from "@material-ui/core/CircularProgress";
import SignInApi from "../api/signInApi";
import {
  hideMessage,
  showAuthLoader,
  userFacebookSignIn,
  userGithubSignIn,
  userGoogleSignIn,
  userSignIn,
  userTwitterSignIn,
} from "actions/Auth";

class SignIn extends React.Component {
  constructor() {
    super();
    this.state = {
      email: "demo@example.com",
      password: "demo#123",
      error: false,
      errormes: "",
      loader: false,
    };
  }

  async login() {
    this.setState({ loader: true });
    // var basUrl = "https://enrouteservice.com/api/";
    var Data = {
      username: this.state.email,
      password: this.state.password,
    };
    var self = this;

    var response = await SignInApi(Data);
    if (response.error === false) {
      localStorage.setItem("user_id", response.data.result.related_user._id);
      localStorage.setItem("token", response.data.result.token);
      window.location.reload(false);
      self.setState({ loader: false });
      console.log(response);
    } else {
      self.setState({
        error: true,
        errormes: response.message,
        loader: false,
      });
      console.log(response);
    }
  }

  componentDidUpdate() {
    if (this.state.error) {
      setTimeout(() => {
        this.setState({ error: false });
      }, 100);
    }
    if (localStorage.getItem("token") != null) {
      this.props.history.push("/");
    }
  }

  render() {
    const { email, password } = this.state;
    //const {showMessage, loader, alertMessage} = this.props;
    return (
      <div className="app-login-container d-flex justify-content-center align-items-center animated slideInUpTiny animation-duration-3">
        <div className="app-login-main-content">
          <div
            className="app-logo-content d-flex align-items-center justify-content-center"
            style={{ backgroundColor: "green" }}
          >
            <Link className="logo-lg" to="/" title="Jambo">
              <img
                src={require("assets/images/ebcom_logo.jpg")}
                alt="ebcom"
                title="ebcom"
              />
            </Link>
          </div>

          <div className="app-login-content">
            <div className="app-login-header mb-4">
              <h1>
                <IntlMessages id="appModule.email" />
              </h1>
            </div>

            <div className="app-login-form">
              <form>
                <fieldset>
                  <TextField
                    label={<IntlMessages id="appModule.email" />}
                    fullWidth
                    onChange={(event) =>
                      this.setState({ email: event.target.value })
                    }
                    defaultValue={email}
                    margin="normal"
                    className="mt-1 my-sm-3"
                  />
                  <TextField
                    type="password"
                    label={<IntlMessages id="appModule.password" />}
                    fullWidth
                    onChange={(event) =>
                      this.setState({ password: event.target.value })
                    }
                    defaultValue={password}
                    margin="normal"
                    className="mt-1 my-sm-3"
                  />

                  <div className="mb-3 d-flex align-items-center justify-content-between">
                    <Button
                      onClick={() => {
                        //this.props.showAuthLoader();
                        //this.props.userSignIn({email, password});
                        this.login();
                      }}
                      variant="contained"
                      color="primary"
                    >
                      <IntlMessages id="appModule.signIn" />
                    </Button>

                    {/* <Link to="/signup">
                      <IntlMessages id="signIn.signUp" />
                    </Link> */}
                  </div>

                  {/* <div className="app-social-block my-1 my-sm-3">
                    <IntlMessages id="signIn.connectWith" />
                    <ul className="social-link">
                      <li>
                        <IconButton
                          className="icon"
                          onClick={() => {
                            this.props.showAuthLoader();
                            this.props.userFacebookSignIn();
                          }}
                        >
                          <i className="zmdi zmdi-facebook" />
                        </IconButton>
                      </li>

                      <li>
                        <IconButton
                          className="icon"
                          onClick={() => {
                            this.props.showAuthLoader();
                            this.props.userTwitterSignIn();
                          }}
                        >
                          <i className="zmdi zmdi-twitter" />
                        </IconButton>
                      </li>

                      <li>
                        <IconButton
                          className="icon"
                          onClick={() => {
                            this.props.showAuthLoader();
                            this.props.userGoogleSignIn();
                          }}
                        >
                          <i className="zmdi zmdi-google-plus" />
                        </IconButton>
                      </li>

                      <li>
                        <IconButton
                          className="icon"
                          onClick={() => {
                            this.props.showAuthLoader();
                            this.props.userGithubSignIn();
                          }}
                        >
                          <i className="zmdi zmdi-github" />
                        </IconButton>
                      </li>
                    </ul>
                  </div> */}
                </fieldset>
              </form>
            </div>
          </div>
        </div>
        {this.state.loader && (
          <div className="loader-view">
            <CircularProgress />
          </div>
        )}
        {this.state.error && NotificationManager.error(this.state.errormes)}
        <NotificationContainer />
      </div>
    );
  }
}

const mapStateToProps = ({ auth }) => {
  const { loader, alertMessage, showMessage, authUser } = auth;
  return { loader, alertMessage, showMessage, authUser };
};

export default connect(mapStateToProps, {
  userSignIn,
  hideMessage,
  showAuthLoader,
  userFacebookSignIn,
  userGoogleSignIn,
  userGithubSignIn,
  userTwitterSignIn,
})(SignIn);
